var drake = dragula({
  copy: function (el, source) {
    return source === getContainer('left-copy-1tomany');
  },
  accepts: function (el, target, source, sibling) {
    // prevent dragged containers from trying to drop inside itself
    return ( (!contains(el,target)) && (target !== getContainer('left-copy-1tomany')) );
  }
});

drake.containers.push(getContainer('left-copy-1tomany' ));
drake.containers.push(getContainer('right-copy-1tomany'));
drake.containers.push(getContainer('teste'));



function getContainer(containerId){
	return document.getElementById(containerId);
}

function contains(a, b){
  return a.contains ?
    a != b && a.contains(b) :
    !!(a.compareDocumentPosition(b) & 16);
}